mapper.select:
SELECT store.* 
FROM store


        (store.name LIKE CONCAT('%', #{keyword}, '%')
        OR store.jibeon LIKE CONCAT('%', #{keyword}, '%')
        OR store.doro LIKE CONCAT('%', #{keyword}, '%'))


        AND store.category IN

            #{cate}




        AND LEFT(adm_code,2) = #{type1} 


        AND LEFT(adm_code,2) = #{type1} 
        AND  LEFT(adm_code, 5) = #{type2}


LIMIT #{maxIndex} OFFSET ( #{page} - 1 ) * 10

SELECT count(store.*) 
FROM store


        (store.name LIKE CONCAT('%', #{keyword}, '%')
        OR store.jibeon LIKE CONCAT('%', #{keyword}, '%')
        OR store.doro LIKE CONCAT('%', #{keyword}, '%'))


        AND store.category IN

            #{cate}




        AND LEFT(adm_code,2) = #{type1} 


        AND LEFT(adm_code,2) = #{type1} 
        AND  LEFT(adm_code, 5) = #{type2}

SELECT 
    jibeon, 
    doro, 
    name, 
    category, 
    lon, 
    lat,
    public.ST_Distance(
        public.ST_SetSRID(
            public.ST_MakePoint(lon, lat), 3857
        ),
        public.ST_Transform(
            public.ST_SetSRID(
                public.ST_MakePoint(#{lon}, #{lat}), 4326
            ), 3857
        )
    ) AS distance_in_meter
FROM 
    store

    public.ST_DWithin(
        public.ST_SetSRID(
            public.ST_MakePoint(lon, lat), 3857
        ),
        public.ST_Transform(
            public.ST_SetSRID(
                public.ST_MakePoint(#{lon}, #{lat}), 4326
            ), 3857
        ),
        #{type2}::float8
    )

        AND name LIKE CONCAT('%', #{keyword}, '%')


        AND category IN

            #{cate}



ORDER BY distance_in_meter ASC
LIMIT #{maxIndex} OFFSET ( #{page} - 1 ) * 10

   SELECT 
    count(*)
FROM 
    store

    public.ST_DWithin(
        public.ST_SetSRID(
            public.ST_MakePoint(lon, lat), 3857
        ),
        public.ST_Transform(
            public.ST_SetSRID(
                public.ST_MakePoint(#{lon}, #{lat}), 4326
            ), 3857
        ),
        #{type2}::float8
    )

        AND name LIKE CONCAT('%', #{keyword}, '%')


        AND category IN

            #{cate}
